",\"affiliation-country\":\"Thailand\"},{\"@_fa\": \"true\", \"affiliation-url\":\"https://api.elsevier.com/content/affiliation/affiliation_id/60017165\",\"afid\":\"60017165\",\"affilname\":\"Khon Kaen University\",\"affiliation-city\":\"Khon Kaen\",\"affiliation-country\":\"Thailand\"},{\"@_fa\": \"true\", \"affiliation-url\":\"https://api.elsevier.com/content/affiliation/affiliation_id/60026326\",\"afid\":\"60026326\",\"affilname\":\"Suranaree University of Technology\",\"affiliation-city\":\"Nakhon Ratchasima\",\"affiliation-country\":\"Thailand\"}],\"prism:aggregationType\":\"Journal\",\"subtype\":\"ar\",\"subtypeDescription\":\"Article\",\"author-count\":{\"@limit\": \"100\", \"$\" :\"4\"},\"author\": [{\"@_fa\": \"true\", \"@seq\": \"1\", \"author-url\":\"https://api.elsevier.com/content/author/author_id/54683571200\",\"authid\":\"54683571200\",\"authname\":\"Dechsupa C.\",\"surname\":\"Dechsupa\",\"given-name\":\"Chanon\",\"initials\":\"C.\",\"afid\": [{\"@_fa\": \"true\", \"$\" :\"60017165\"}]},{\"@_fa\": \"true\", \"@seq\": \"2\", \"author-url\":\"https://api.elsevier.com/content/author/author_id/16310845100\",\"authid\":\"16310845100\",\"authname\":\"Prasankok P.\",\"surname\":\"Prasankok\",\"given-name\":\"Pongpun\",\"initials\":\"P.\",\"afid\": [{\"@_fa\": \"true\", \"$\" :\"60026326\"}]},{\"@_fa\": \"true\", \"@seq\": \"3\", \"author-url\":\"https://api.elsevier.com/content/author/author_id/6507688023\",\"authid\":\"6507688023\",\"authname\":\"Vattanawood W.\",\"surname\":\"Vattanawood\",\"given-name\":\"Wiwat\",\"initials\":\"W.\",\"afid\": [{\"@_fa\": \"true\", \"$\" :\"60028190\"}]},{\"@_fa\": \"true\", \"@seq\": \"4\", \"author-url\":\"https://api.elsevier.com/content/author/author_id/6507723368\",\"authid\":\"6507723368\",\"authname\":\"Thongtak A.\",\"surname\":\"Thongtak\",\"given-name\":\"Arthit\",\"initials\":\"A.\",\"afid\": [{\"@_fa\": \"true\", \"$\" :\"60028190\"}]}],\"authkeywords\":\"bivalves image | deep learning | Image processing | Mob